package javaprogramming;

public class testq5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=500;
		System.out.println(num);
		if(num>=400)
		{
			num-=100;
			System.out.println(num);
			if(num>=300)
			{
				num-=100;
				System.out.println(num);
				if(num>=200)
				{
					num-=100;
				System.out.println(num);
				if(num>=100)
				{
					num-=100;
					System.out.println(num);
					if(num>=100)
						num-=100;
					System.out.println(num);
				}
				}
			}
			
		}

	}

}
